head.script:
function loadStyle(){
  $("#PageList1 ul").addClass('navbar-nav mr-auto page-list');
  $("#PageList1 li").addClass('nav-item btn btn read-more-btn');
  $("#PageList1 a").addClass('nav-link');
  }

if(document.readyState === 'loading') {
  window.addEventListener('DOMContentLoaded', loadStyle);
} else {
  loadStyle();
}

